import React, { useState,useEffect } from "react";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup'
import Placeholder from 'react-bootstrap/Placeholder';

function News(){
    const [news,setNews]=useState([]);
    const [load,setLoad]=useState(false);
    const getNews=async()=>{
        try{
            setLoad(true);
            const url = 'https://bing-news-search1.p.rapidapi.com/news?count=100&safeSearch=Strict&textFormat=Raw';
            const options = {
                method: 'GET',
                headers: {
                    'X-BingApis-SDK': 'true',
                    'X-RapidAPI-Key': 'd99b866f93msh7df806d15ecda12p1a32f0jsn827a5a395ac8',
                    'X-RapidAPI-Host': 'bing-news-search1.p.rapidapi.com'
                }
            };

            const response = await fetch(url, options);
            const ans = await response.text();
            const data=JSON.parse(ans);
            console.log(data);
            setLoad(false);
            setNews(data.value);
        } catch (error) {
            console.error(error);
        }
    }
    useEffect(()=>{
        getNews();
      },[]);
    return(
        <div>
        <Card style={{height:"400px", overflowY:"scroll"}}>
        <Card.Header>LATEST NEWS</Card.Header>
        <Card.Body style={{overflowY:"scroll"}}>
            {load===true?(
                <>
                <Placeholder xs={6} />
                <Placeholder className="w-75" /> <Placeholder style={{ width: '25%' }} />
                </>
            ):(
                (news.length>0)?(news.slice(0,5).map(n=>
                    <Card style={{ width: '18rem',marginBottom:"10px" }}>
                    <ListGroup variant="flush">
                        <ListGroup.Item><a href={n.url}>{n.name}</a>
                        <b> {n.datePublished.slice(0,10)}</b>
                        </ListGroup.Item>
                        <ListGroup.Item>{n.description}</ListGroup.Item>
                    </ListGroup>
                    </Card>
                )):(<></>)
            )}

        </Card.Body>
        </Card>

        </div>
    )
}
export default News;
