import React,{useState,useEffect} from "react";
import Events from "./Events";
// import { NavLink } from "react-router-dom";
import News from "./News";
import Button from "react-bootstrap/Button";
import Card from "./Card";
import Jobs from "./Jobs";
function Home(){
    return(
        <div className="d-flex flex-row w-100 justify-content-between">
            <div className="d-flex flex-row col-11 col-md-9" style={{height:"85vh"}}>
            <div className="d-none d-md-block col-sm-4" style={{height:"300px",padding:"1rem"}}>
                <Events/>
            </div>
            <div className="col-11 col-md-8" style={{ height: "250px", padding: "1rem", textAlign: "center", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center" }}>
  <h3>Discover what you are looking for by searching blog posts...</h3>
  <br></br>
  <form action="/search" method="post">
    <div className="input-group">
      <input type="text" className="input-group-field form-control" name="searchString" placeholder="Search posts" style={{ borderRadius: "10px" }} autoComplete="off" />
      <button type="submit" className="input-group-button btn btn-warning btn-lg">Search</button>
    </div>
  </form>
</div>

            <div>Recent Posts</div>
            </div>
            <div className="d-none d-md-block col-sm-3" style={{height:"85vh",padding:"1rem"}}>
                <News/>
                <Jobs/>
            </div>
        </div>
    )
}
export default Home;
