import React,{useState,useEffect} from "react";
import Events from "./Events";
import News from "./News";
import Jobs from "./Jobs";
import Blog from "./Blog";
function Home(){
    const [search,setSearch]=useState([]);
    const [searchStr,setSearchStr]=useState("");
    function handleChange(event){
        const value=event.target.value;
        setSearchStr(value);
    }
    const handleSearch=async(event)=>{
        event.preventDefault();
        try {
        const res=await fetch("/search",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:
                searchStr
        });
        const data=await res.json();
            setSearch(data);
            console.log(data);
        }catch (error) {
            console.log(error);
        }
      }
    return(
        <div className="d-flex flex-row w-100 justify-content-between">
            <div className="d-flex flex-row col-11 col-md-9" style={{height:"85vh"}}>
            <div className="d-none d-md-block col-sm-4" style={{height:"300px",padding:"1rem"}}>
                <News/>
            </div>
            <div className="col-11 col-md-8" style={{height:"85vh",padding:"1rem",textAlign:"center",alignItems:"center",marginTop:"25px"}}>
                <h1>Discover what you are looking for by searching blog posts...</h1>
                <br></br>

                <form method="POST">
                    <div class = "input-group">
                    <input type="text" class="input-group-field form-control" placeholder="Search posts" style={{bordeRadius: "10px"}} autocomplete="off" name="searchStr" onChange={handleChange} value={searchStr}/>
                    <button type="submit" class="input-group-button btn btn-warning btn-lg" onClick={handleSearch}>Search</button>
                    </div>
                </form>

                <div style={{textAlign:"left", marginTop:"20px"}}>
                    <br></br>
                    <h3>Recent Posts</h3>
                    <Blog/>
                </div>

            </div>
            </div>
            <div className="d-none d-md-block col-sm-3">
                <Events/>
                <Jobs/>
            </div>
        </div>
    )
}
export default Home;
