import React, { useState,useEffect } from "react";
import Card from 'react-bootstrap/Card';
import ListGroupItem from "react-bootstrap/ListGroupItem";

function MNews(){
    const [news,setNews]=useState([]);
    const [load,setLoad]=useState(false);
    const getNews=async()=>{
        try{
            setLoad(true);
            const url = 'https://bing-news-search1.p.rapidapi.com/news?count=100&safeSearch=Strict&textFormat=Raw';
            const options = {
                method: 'GET',
                headers: {
                    'X-BingApis-SDK': 'true',
                    'X-RapidAPI-Key': 'd99b866f93msh7df806d15ecda12p1a32f0jsn827a5a395ac8',
                    'X-RapidAPI-Host': 'bing-news-search1.p.rapidapi.com'
                }
            };

            const response = await fetch(url, options);
            const ans = await response.text();
            const data=JSON.parse(ans);
            setLoad(false);
            setNews(data.value);
        } catch (error) {
            console.error(error);
        }
    }
    useEffect(()=>{
        getNews();
      },[]);
    return(
        <div className="base">
        <h2>All Posts:</h2>
        <div className="reverse" style={{padding:"20px",justifyItems:"center",height:"75vh",overflowY:"scroll"}}>
            {(load===true)?(

                <>

                </>
            ):(
                (news.length>0)?(news.slice(0,5).map(n=>
                    <Card className="card w-75 m-auto">
                    <ListGroup variant="flush">
                        <ListGroup.Item><a href={n.url}>{n.name}</a>
                        <br></br>
                        <b>Posted at:</b> {n.datePublished.slice(11,19)}
                        </ListGroup.Item>
                        <ListGroup.Item>{n.description}</ListGroup.Item>
                    </ListGroup>
                    </Card>
                )):(<></>))}
        </div>
        </div>
    )
}
export default MNews;
