import React, { useState,useEffect } from "react";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup'
function News(){
    const [news,setNews]=useState({});
    const [load,setLoad]=useState(false);
    const getNews=async()=>{
        try{
            setLoad(true);
            const url = 'https://bing-news-search1.p.rapidapi.com/news?count=100&safeSearch=Strict&textFormat=Raw';
            const options = {
                method: 'GET',
                headers: {
                    'X-BingApis-SDK': 'true',
                    'X-RapidAPI-Key': 'd99b866f93msh7df806d15ecda12p1a32f0jsn827a5a395ac8',
                    'X-RapidAPI-Host': 'bing-news-search1.p.rapidapi.com'
                }
            };

            const response = await fetch(url, options);
            const ans = await response.text();
            const data=JSON.parse(ans);
            setLoad(false);
            setNews(data);
        } catch (error) {
            console.error(error);
        }
    }
    useEffect(()=>{
        getNews();
      },[]);
    return(
        <div>
            <button onClick={getNews}>Get News</button>

        <Card>
        <Card.Header>NEWS</Card.Header>
        <Card.Body>
            if(load==false){
                <>
                <Placeholder xs={6} />
                <Placeholder className="w-75" /> <Placeholder style={{ width: '25%' }} />
                </>
            }
            {(news.length>0 && load===false)?(news.value.map((n)=>{
                <Card style={{ width: '18rem' }}>
                <ListGroup variant="flush">
                    <ListGroup.Item>{n.name}</ListGroup.Item>
                    <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                    <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
                </ListGroup>
                </Card>
            })):(<></>)}

        </Card.Body>
        </Card>

        </div>
    )
}
export default News;
