import React, { useState,useEffect } from "react";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup'
import Placeholder from 'react-bootstrap/Placeholder';

function Jobs(){
    const [jobs,setJobs]=useState([]);
    const [load,setLoad]=useState(false);
    const getJobs=async()=>{
        try{
            setLoad(true);
            const res=await fetch("/jobs",{
                method:"GET",
                headers:{
                    "Content-Type":"application/json"
                }
            });
            if(res.status===200){
                const response=await res.json();
                // console.log(data[0]);
                const data=JSON.parse(response);
                setJobs(data);
                setLoad(false);
                console.log(data);
            }
        } catch (error) {
            console.error(error);
        }
    }
    useEffect(()=>{
        getJobs();
      },[]);
    return(
        <div style={{marginTop:"25px"}}>
        <Card style={{height:"200px", overflowY:"scroll"}}>
        <Card.Header>NEW JOBS</Card.Header>
        <Card.Body style={{overflowY:"scroll"}}>
            {load===true?(
                <>
                <Placeholder xs={6} />
                <Placeholder className="w-75" /> <Placeholder style={{ width: '25%' }} />
                </>
            ):(
                (jobs.length>0)?(jobs.slice(0,2).map(job=>
                    <Card style={{ width: '18rem',marginBottom:"10px" }}>
                    <ListGroup variant="flush">
                        <ListGroup.Item><a href={job.url}>{job.name}</a></ListGroup.Item>
                        <ListGroup.Item>{job.description}</ListGroup.Item>
                    </ListGroup>
                    </Card>
                )):(<></>)
            )}

        </Card.Body>
        </Card>

        </div>
    )
}
export default Jobs;
