import React,{useState,useEffect} from "react";

function Result({c}){
    return(
        <>
            {c.map((r)=>{
            return(
                <>
            <div>
                <div className="card">
                    <div className="card-body">
                    <h2 className="card-title">{r.title}</h2>
                    <div className="dte"><strong>{r.date}</strong></div>
                    <p className="card-text">{r.post}
                        </p>
                        <div className="dte">
                        <em>Posted by: {r.writer}</em>
                        </div>
                    </div>
                </div>
                <br/>
            </div>
                </>
            )})}

        </>
    )
}
export default Result;
